package j0327;

public class C0327_01 {

	// 슬래시 두개 = 한줄주석
//    컨트롤 + / = 주석처리
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("안녕하세요");
		System.out.print("hi"); // 줄바꿈 포함 안됨
		System.out.println("Hello"); // 줄바꿈이 포함됨
		
		// print : 줄바꿈 없음
		// println : 줄바꿈 한다
		
		System.out.println("10+3");
		// " " 안의 문장이 그대로 출력
		System.out.println(10+3);
		// "" 없으면 계산결과 처리해줌
		System.out.println(10-3);
		

	}

}