package j0327;

public class C0327_06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	    // 변수 선언하기
		// 변수타입 변수명 = 값
		
		// 변수명 pwoer로 하고 값을 true를 넣는다
		
		// 변수명 ch, 값을 A를 넣는다
		
		// 변수명 tab, 값 \t (char)
		
		// 변수명 b 값 127
		// 변수명 s 값 32767
		// 변수명 i 값 100
		// 변수명 l 값 1000000000000
		// 변수명 f 값 3.14
		// 변수명 d 3.14
		
		boolean power = true;
		
        char ch = 'A';
        
        char tab = '\t';
     	// \t : 들여쓰기 \n : 줄바꿈 	        
        byte b = 127;
        
        short s = 32767;
        
        int i = 100;
        
        long l = 1000000000000L;
        
        float f = 3.14f;
        
        double d = 3.14;
        
        System.out.println(power);
        System.out.println(ch);
        System.out.println(tab);
        System.out.println(b);
        System.out.println(s);
        System.out.println(i);
        System.out.println(l);
        System.out.println(f);
        System.out.println(d);
        
        		
        
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	}

}
